class ClsMediaInspectorTool {
  #appVersion = "0.0.2";

  #mediaSrcs = [];
  #mediaRowData = [];

  constructor() {
    this.init();
  }

  init() {
    console.log("CldMediaInspectorTool Version", this.#appVersion);
  }
}

function appMain() {
  var objMiTool;

  try {
    if (!objMiTool || typeof objMiTool === "undefined")
      objMiTool = new ClsMediaInspectorTool();
  } catch (error) {
    console.log("ClsMediaInspectorTool already instantiated");
  }

  const getResourcesPerfData = () => {
    const duration = performance.getEntriesByType("navigation")[0].duration;

    if (!duration) setTimeout(getResourcesPerfData, 10);
    else {
      console.log("We are still ok at this point ...");
    }
  };

  getResourcesPerfData();
}

appMain();
